INSERT INTO `trivia` VALUES (196,'{\n\"pregunta\" : \"In Mexica cosmology, which of the following BEST describes the relationship between the vertical and cardinal directional systems?\",\n\"opciones\" : [\"The vertical and cardinal systems were completely separate and unrelated.\", \"The cardinal directions were layered vertically, with each direction having its own thirteen celestial and nine underworld levels.\", \"The center point, Xjuhtecuhtli\'s realm, was the intersection of the thirteen celestial layers and the four cardinal directions.\", \"The vertical system was superimposed onto the cardinal system, with Omeyocan located directly above Xjuhtecuhtli\'s realm.\"],\n\"respuesta\" : \"The center point, Xjuhtecuhtli\'s realm, was the intersection of the thirteen celestial layers and the four cardinal directions.\"\n}\n',NULL,NULL,24),(197,'{\n\"pregunta\" : \"In Mexica cosmology, which deity ruled the underworld, Mictlan?\",\n\"opciones\" : [\"Xipe Totec\", \"Huitzilopochtli\", \"Mictlantecuhtli\", \"Xochipilli\"],\n\"respuesta\" : \"Mictlantecuhtli\"\n}\n',NULL,NULL,24),(198,'{\n\"pregunta\": \"According to the summary, what is the primary function theorized for the Aztec Calendar Stone during the Tlacaxipehualiztli ceremony?\",\n\"opciones\": [\"A religious shrine for prayer\", \"A platform for gladiatorial sacrifices\", \"An astronomical observatory\", \"A royal burial site\"],\n\"respuesta\": \"A platform for gladiatorial sacrifices\"\n}\n',NULL,NULL,24),(199,'{\n\"pregunta\" : \"In Mexica cosmology, which cardinal direction was associated with the god Huitzilopochtli and what color represented it?\",\n\"opciones\" : [\"East, Red\", \"West, White\", \"North, Black\", \"South, Blue\"],\n\"respuesta\" : \"South, Blue\"\n}\n',NULL,NULL,24),(200,'{\n\"pregunta\" : \"In Mexica cosmology, which cardinal direction was associated with Huitzilopochtli\'s domain?\",\n\"opciones\" : [\"East\", \"West\", \"North\", \"South\"],\n\"respuesta\" : \"South\"\n}\n',NULL,NULL,24),(201,'{\n\"pregunta\" : \"According to pre-Hispanic cosmology, what was the primary reason for human sacrifice?\",\n\"opciones\" : [\"To appease angry gods\", \"To ensure a bountiful harvest\", \"To maintain the sun\'s power and provide life-giving energy\", \"To honor the deceased and their ancestors\"],\n\"respuesta\" : \"To maintain the sun\'s power and provide life-giving energy\"\n}\n',NULL,NULL,24),(202,'{\n\"pregunta\" : \"According to the provided text, which of the following BEST describes the pre-Hispanic Mexican approach to medicine?\",\n\"opciones\" : [\"Relied solely on plant-based remedies\", \"Was unsophisticated and lacked a formal system\", \"Integrated knowledge from diverse sources, including plants, animals, minerals, and obsidian\", \"Was primarily focused on religious rituals and lacked practical applications\"],\n\"respuesta\" : \"Integrated knowledge from diverse sources, including plants, animals, minerals, and obsidian\"\n}\n',NULL,NULL,24),(203,'{\n\"pregunta\" : \"According to the provided text, what is the primary purpose theorized for the Aztec Calendar Stone?\",\n\"opciones\" : [\"A decorative element for the Mexica palace\", \"An astronomical observatory for charting celestial events\", \"A gladiatorial sacrifice platform during the Tlacaxipehualiztli ceremony\", \"A ceremonial vessel for offerings to the gods\"],\n\"respuesta\" : \"A gladiatorial sacrifice platform during the Tlacaxipehualiztli ceremony\"\n}\n',NULL,NULL,24),(204,'{\"pregunta\" : \"In Mexica cosmology, which deity ruled the highest celestial layer, Omeyocan?\", \"opciones\" : [\"Xipe Totec\", \"Huitzilopochtli\", \"Mictlantecuhtli\", \"Creator Gods\"], \"respuesta\" : \"Creator Gods\"}\n',NULL,NULL,24),(205,'{\n\"pregunta\": \"In Mexica cosmology, which of the following BEST describes the relationship between the vertical and cardinal directional systems?\",\n\"opciones\": [\"The vertical and cardinal systems were entirely separate and unrelated.\", \"The cardinal directions were layered vertically, with each direction having its own thirteen celestial and nine underworld levels.\", \"The vertical axis intersected the cardinal directions at a central point, unifying the cosmic forces.\", \"The cardinal directions determined the specific gods inhabiting each level of the vertical structure.\"],\n\"respuesta\": \"The vertical axis intersected the cardinal directions at a central point, unifying the cosmic forces.\"\n}\n',NULL,NULL,24),(206,'{\n  \"pregunta\": \"According to pre-Hispanic cosmology, what was the primary reason for human sacrifice?\",\n  \"opciones\": [\"To appease angry gods.\", \"To ensure a bountiful harvest.\", \"To maintain the sun\'s power and provide life-giving light.\", \"To honor the deceased.\"],\n  \"respuesta\": \"To maintain the sun\'s power and provide life-giving light.\"\n}\n',NULL,NULL,24),(207,'{\n\"pregunta\" : \"What best exemplifies the pre-Hispanic Mexican understanding of herbal medicine, as evidenced by the provided text?\",\n\"opciones\" : [\"The Spanish conquest\'s impact on native healing practices\", \"The limited use of plants in their medicinal system\", \"The reverence for the goddess Tzapotlatena and her discovery of resin\'s medicinal properties\", \"The exclusive use of obsidian in all treatments\"],\n\"respuesta\" : \"The reverence for the goddess Tzapotlatena and her discovery of resin\'s medicinal properties\"\n}\n',NULL,NULL,24);
